<div>&lt;!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN"</div><div>"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd"&gt;</div><div>&lt;html xmlns="http://www.w3.org/1999/xhtml" xml:lang="fr"&gt;</div><div><span style="white-space:pre">	</span>&lt;head&gt;</div><div><span style="white-space:pre">		</span>&lt;title&gt;Mon blog&lt;/title&gt;</div><div><span style="white-space:pre">		</span>&lt;meta http-equiv="Content-Type" content="text/html;</div><div><span style="white-space:pre">		</span>charset=iso-8859-1" /&gt;</div><div><span style="white-space:pre">		</span>&lt;link href="style.css" rel="stylesheet" type="text/css" /&gt;</div><div><span style="white-space:pre">	</span>&lt;/head&gt;</div><div><span style="white-space:pre">	</span>&lt;body&gt;</div><div><span style="white-space:pre">		</span>&lt;h1&gt;Mon super blog !&lt;/h1&gt;</div><div><span style="white-space:pre">		</span>&lt;p&gt;Derniers billets du blog :&lt;/p&gt;</div><div><span style="white-space:pre">		</span>&lt;?php</div><div><span style="white-space:pre">		</span>// Connexion à la base de données</div><div><span style="white-space:pre">		</span>try</div><div><span style="white-space:pre">		</span>{</div><div><span style="white-space:pre">			</span>$bdd = new PDO('mysql:host=localhost;dbname=test', 'root', '');</div><div><span style="white-space:pre">		</span>}catch(Exception $e)</div><div><span style="white-space:pre">		</span>{</div><div><span style="white-space:pre">			</span>die('Erreur : '.$e-&gt;getMessage());</div><div><span style="white-space:pre">		</span>}</div><div><span style="white-space:pre">		</span>// On récupère les 5 derniers billets</div><div><span style="white-space:pre">		</span>$req = $bdd-&gt;query('SELECT id, titre, contenu,</div><div><span style="white-space:pre">			</span>DATE_FORMAT(date_creation, \'%d/%m/%Y à %Hh%imin%ss\') AS</div><div><span style="white-space:pre">			</span>date_creation_fr FROM billets ORDER BY date_creation DESC LIMIT 0,</div><div><span style="white-space:pre">			</span>5');</div><div><span style="white-space:pre">		</span>while ($donnees = $req-&gt;fetch())</div><div><span style="white-space:pre">		</span>{</div><div><span style="white-space:pre">		</span>?&gt;</div><div><span style="white-space:pre">		</span>&lt;div class="news"&gt;</div><div><span style="white-space:pre">		</span>&lt;h3&gt;</div><div><span style="white-space:pre">		</span>&lt;?php echo htmlspecialchars($donnees['titre']); ?&gt;</div><div><span style="white-space:pre">		</span>&lt;em&gt;le &lt;?php echo $donnees['date_creation_fr']; ?&gt;&lt;/em&gt;</div><div><span style="white-space:pre">		</span>&lt;/h3&gt;</div><div><span style="white-space:pre">		</span>&lt;p&gt;</div><div><span style="white-space:pre">		</span>&lt;?php</div><div><span style="white-space:pre">		</span>// On affiche le contenu du billet</div><div><span style="white-space:pre">		</span>echo nl2br(htmlspecialchars($donnees['contenu']));</div><div><span style="white-space:pre">		</span>?&gt;</div><div><span style="white-space:pre">		</span>&lt;br /&gt;</div><div><span style="white-space:pre">		</span>&lt;em&gt;&lt;a href="commentaires.php?billet=&lt;?php echo $donnees['id'];</div><div><span style="white-space:pre">		</span>?&gt;"&gt;Commentaires&lt;/a&gt;&lt;/em&gt;</div><div><span style="white-space:pre">		</span>&lt;/p&gt;</div><div><span style="white-space:pre">		</span>&lt;/div&gt;</div><div><span style="white-space:pre">		</span>&lt;?php</div><div><span style="white-space:pre">		</span>} // Fin de la boucle des billets</div><div><span style="white-space:pre">		</span>$req-&gt;closeCursor();</div><div><span style="white-space:pre">		</span>?&gt;</div><div><span style="white-space:pre">	</span>&lt;/body&gt;</div><div>&lt;/html&gt;</div>